import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.css';
import { UserContext } from './UserContext';
import Navbar from './Navbar';
import Footer from './Footer';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        profile_photo: null,
        newPhotoPreview: null
    });
    const navigate = useNavigate();
    const { user: contextUser, logout, login } = useContext(UserContext);

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const storedUser = localStorage.getItem('user');
                if (!storedUser) {
                    setError('Пользователь не найден. Авторизуйтесь.');
                    setLoading(false);
                    return navigate('/login');
                }

                const userData = JSON.parse(storedUser);
                setUser(userData);
                setFormData({
                    username: userData.username,
                    email: userData.email,
                    profile_photo: null,
                    newPhotoPreview: userData.profile_photo
                });
            } catch (err) {
                setError('Ошибка загрузки профиля');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData({
                ...formData,
                profile_photo: file,
                newPhotoPreview: URL.createObjectURL(file)
            });
        }
    };

    const handleSave = async () => {
        if (!user) return;

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('username', formData.username);
            formDataToSend.append('email', formData.email);
            if (formData.profile_photo) {
                formDataToSend.append('profile_photos', formData.profile_photo); // Обратите внимание на имя поля 'profile_photos'
            }

            const response = await axios.put(
                `http://127.0.0.1:8000/accounts/api/users/${user.id}/edit/`,
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            if (response.data.status === 'success') {
                // Получаем обновленные данные профиля
                const profileResponse = await axios.get(
                    `http://127.0.0.1:8000/accounts/api/profile/?userid=${user.id}`
                );

                const updatedUser = {
                    ...user,
                    username: formData.username,
                    email: formData.email,
                    profile_photo: profileResponse.data.profile_photo || user.profile_photo,
                };

                // Обновляем контекст и localStorage
                login(updatedUser);
                setUser(updatedUser);
                setFormData(prev => ({
                    ...prev,
                    newPhotoPreview: profileResponse.data.profile_photo
                }));
                setIsEditing(false);
            } else {
                setError(response.data.message || 'Ошибка при обновлении профиля');
            }
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при отправке данных на сервер');
            console.error('Update error:', error);
        }
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="profile-container">
            <Navbar />
            {user ? (
                <div className="profile-content">
                    <img
                        src={formData.newPhotoPreview ||
                            (user.profile_photo ?
                                `http://127.0.0.1:8000${user.profile_photo}` :
                                '/default-profile.png')
                        }
                        alt="Profile"
                        className="profile-photo"
                    />
                    {isEditing ? (
                        <div className="edit-form">
                            <div className="form-group">
                                <label>Имя пользователя:</label>
                                <input
                                    type="text"
                                    name="username"
                                    value={formData.username}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Email:</label>
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Фото профиля:</label>
                                <input
                                    type="file"
                                    name="profile_photo"
                                    accept="image/*"
                                    onChange={handleFileChange}
                                />
                            </div>
                            <div className="form-buttons">
                                <button
                                    className="save-button"
                                    onClick={handleSave}
                                    disabled={!formData.username || !formData.email}
                                >
                                    Сохранить
                                </button>
                                <button
                                    className="cancel-button"
                                    onClick={() => {
                                        setIsEditing(false);
                                        setFormData({
                                            username: user.username,
                                            email: user.email,
                                            profile_photo: null,
                                            newPhotoPreview: user.profile_photo
                                        });
                                    }}
                                >
                                    Отмена
                                </button>
                            </div>
                        </div>
                    ) : (
                        <div className="profile-details">
                            <h2>{user.username}</h2>
                            <p>Email: {user.email}</p>
                            <div className="profile-buttons">
                                <button className="nav-button" onClick={() => navigate(`/`)}>На главную</button>
                                <button className="edit-button" onClick={() => setIsEditing(true)}>Редактировать</button>
                                <button className="logout-button" onClick={handleLogout}>Выйти</button>
                            </div>
                        </div>
                    )}
                </div>
            ) : (
                <p>Данные профиля не найдены.</p>
            )}
            <Footer />
        </div>
    );
};

export default Profile;
