import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);

  const fetchLessonDetails = async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
      setLesson(response.data);
      setFiles(response.data.materials || []);
    } catch (err) {
      setError('Ошибка при загрузке данных занятия');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchLessonDetails();
  }, [lessonId]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();

    if (!file) return alert("Выберите файл");

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/upload_files/`,
        formData
      );

      if (response.data.status === 'success') {
        alert('Файл успешно добавлен');
        setFile(null);
        fetchLessonDetails(); // перезагрузка урока с обновленным списком файлов
      }
    } catch (err) {
      alert('Ошибка при добавлении файла');
      console.error(err);
    }
  };

  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!lesson) return <p>Загрузка...</p>;

  return (
    <div>
      <Navbar />
      <div className="lesson-detail-container">
        <h2>{lesson.title}</h2>
        <p>{lesson.description}</p>

        <h3>Файлы</h3>
        <form onSubmit={handleFileUpload}>
          <input type="file" onChange={handleFileChange} />
          <button type="submit">Загрузить файл</button>
        </form>

        <ul>
          {files.length > 0 ? (
            files.map((file, index) => (
              <li key={index}>
                <a
                  href={file.file} // ← путь к файлу из API
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {file.name || 'Файл'}
                </a>
              </li>
            ))
          ) : (
            <p>Файлы отсутствуют</p>
          )}
        </ul>
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
