import React, { useEffect, useState } from 'react';
//import './Students.css';
import Navbar from './Navbar';
import Footer from './Footer';

const Students = () => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchStudents = async () => {
            const storedUser = localStorage.getItem('user');
            if (!storedUser) {
                setError('Пользователь не авторизован');
                return;
            }

            try {
                const response = await fetch('http://127.0.0.1:8000/api/students/', {
                    credentials: 'include',
                });

                if (!response.ok) {
                    throw new Error('Ошибка при загрузке данных');
                }

                const data = await response.json();
                if (data.error) {
                    setError(data.error);
                } else {
                    setStudents(data.students);
                }
            } catch (err) {
                setError('Не удалось загрузить список учеников.');
                console.error(err);
            }
        };

        fetchStudents();
    }, []);

    return (
        <div className="students-container">
            <Navbar />
            <h2>Список учеников</h2>
            {error && <p className="error">{error}</p>}
            <ul className="student-list">
                {students.map(student => (
                    <li key={student.id} className="student-card">
                        <img src={student.profile_photo || 'default-profile.png'} alt="avatar" className="avatar" />
                        <div>
                            <p><strong>Имя:</strong> {student.username}</p>
                            <p><strong>Email:</strong> {student.email}</p>
                        </div>
                    </li>
                ))}
            </ul>
            <Footer />
        </div>
    );
};

export default Students;
