import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import './Profile.css';
import { UserContext } from './UserContext';
import Navbar from './Navbar';
import Footer from './Footer';

const Profile = () => {
    const { userid } = useParams();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        profile_photo: null,
        newPhotoPreview: null
    });
    const navigate = useNavigate();
    const { user: contextUser, logout, login } = useContext(UserContext);

    useEffect(() => {
        const fetchProfile = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:8000/accounts/api/profile/?userid=${userid || contextUser?.id}`);

                if (response.data) {
                    setFormData({
                        username: response.data.username,
                        email: response.data.email,
                        profile_photo: null,
                        newPhotoPreview: response.data.profile_photo
                    });
                }
            } catch (err) {
                setError(err.response?.data?.error || 'Ошибка загрузки профиля');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [userid, contextUser]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData({
                ...formData,
                profile_photo: file,
                newPhotoPreview: URL.createObjectURL(file)
            });
        }
    };

    const handleSave = async () => {
        try {
            const formDataToSend = new FormData();
            formDataToSend.append('username', formData.username);
            formDataToSend.append('email', formData.email);
            if (formData.profile_photo) {
                formDataToSend.append('profile_photos', formData.profile_photo);
            }

            const response = await axios.put(
                `http://127.0.0.1:8000/accounts/api/users/${userid || contextUser?.id}/edit/`,
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            if (response.data.status === 'success') {
                // Обновляем данные пользователя
                const updatedProfile = await axios.get(
                    `http://127.0.0.1:8000/accounts/api/profile/?userid=${userid || contextUser?.id}`
                );

                if (contextUser?.id === updatedProfile.data.id) {
                    login(updatedProfile.data);
                }

                setIsEditing(false);
                setFormData(prev => ({
                    ...prev,
                    newPhotoPreview: updatedProfile.data.profile_photo
                }));
            }
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка обновления профиля');
            console.error('Update error:', error);
        }
    };

    const handleDeleteAccount = async () => {
        if (window.confirm('Вы уверены, что хотите удалить аккаунт? Это действие нельзя отменить.')) {
            try {
                await axios.delete(
                    `http://127.0.0.1:8000/accounts/api/users/${userid || contextUser?.id}/delete/`
                );

                if (!userid || userid === contextUser?.id) {
                    logout();
                    navigate('/');
                } else {
                    navigate('/users');
                }
            } catch (error) {
                setError(error.response?.data?.error || 'Ошибка удаления аккаунта');
            }
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (loading) return <div className="loading">Загрузка профиля...</div>;
    if (error) return <div className="error">{error}</div>;

    const currentUser = userid ? formData : contextUser;
    const isCurrentUserProfile = !userid || userid === contextUser?.id;

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-content">
                <div className="profile-header">
                    <img
                        src={formData.newPhotoPreview ||
                            currentUser?.profile_photo ||
                            '/default-profile.png'
                        }
                        alt="Profile"
                        className="profile-photo"
                    />
                    <h2>{currentUser?.username}</h2>
                    {currentUser?.is_staff && <span className="staff-badge">Администратор</span>}
                </div>

                {isEditing ? (
                    <div className="edit-form">
                        <div className="form-group">
                            <label>Имя пользователя:</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                className="form-input"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className="form-input"
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label>Фото профиля:</label>
                            <input
                                type="file"
                                name="profile_photo"
                                accept="image/*"
                                onChange={handleFileChange}
                                className="form-input"
                            />
                        </div>
                        <div className="form-actions">
                            <button
                                className="btn save-btn"
                                onClick={handleSave}
                                disabled={!formData.username || !formData.email}
                            >
                                Сохранить
                            </button>
                            <button
                                className="btn cancel-btn"
                                onClick={() => {
                                    setIsEditing(false);
                                    setFormData({
                                        username: currentUser.username,
                                        email: currentUser.email,
                                        profile_photo: null,
                                        newPhotoPreview: currentUser.profile_photo
                                    });
                                }}
                            >
                                Отмена
                            </button>
                        </div>
                    </div>
                ) : (
                    <div className="profile-details">
                        <p><strong>Email:</strong> {currentUser?.email}</p>

                        <div className="profile-actions">
                            {isCurrentUserProfile && (
                                <>
                                    <button
                                        className="btn edit-btn"
                                        onClick={() => setIsEditing(true)}
                                    >
                                        Редактировать профиль
                                    </button>
                                    <button
                                        className="btn logout-btn"
                                        onClick={handleLogout}
                                    >
                                        Выйти
                                    </button>
                                    <button
                                        className="btn delete-btn"
                                        onClick={handleDeleteAccount}
                                    >
                                        Удалить аккаунт
                                    </button>
                                </>
                            )}
                        </div>
                    </div>
                )}
            </div>
            <Footer />
        </div>
    );
};

export default Profile;
