import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import './StudentCourses.css';

// Компонент списка курсов
const StudentCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/accounts/api/courses/');
        setCourses(response.data?.courses || []);
      } catch (err) {
        setError('Ошибка загрузки курсов');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (loading) return <div className="loading">Загрузка курсов...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="student-courses-page">
      <Navbar />
      <div className="courses-container">
        <h1>Доступные курсы</h1>
        <div className="courses-grid">
          {courses.map(course => (
            <div key={course.id} className="course-card">
              {course.cover_image && (
                <img 
                  src={course.cover_image} 
                  alt={course.title} 
                  className="course-cover"
                />
              )}
              <div className="course-content">
                <h2>{course.title}</h2>
                <p className="course-description">{course.description}</p>
                <Link to={`/student/courses/${course.id}`} className="view-course-btn">
                  Перейти к курсу
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </div>
  );
};

// Компонент деталей курса (список уроков)
const StudentCourseDetail = () => {
  const { courseId } = useParams();
  const [course, setCourse] = useState(null);
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState({ course: true, lessons: true });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourseData = async () => {
      try {
        const [courseRes, lessonsRes] = await Promise.all([
          axios.get(`http://127.0.0.1:8000/accounts/api/courses/${courseId}/`),
          axios.get(`http://127.0.0.1:8000/accounts/api/courses/${courseId}/lessons/`)
        ]);

        if (courseRes.data?.status === 'success') {
          setCourse(courseRes.data.course);
        }

        if (lessonsRes.data?.status === 'success') {
          setLessons(lessonsRes.data.lessons || []);
        }
      } catch (err) {
        setError('Ошибка загрузки данных курса');
        console.error(err);
      } finally {
        setLoading(prev => ({ ...prev, course: false, lessons: false }));
      }
    };

    fetchCourseData();
  }, [courseId]);

  if (loading.course || loading.lessons) return <div className="loading">Загрузка данных курса...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!course) return <div className="no-data">Курс не найден</div>;

  return (
    <div className="student-course-page">
      <Navbar />
      <div className="course-container">
        <button onClick={() => navigate('/student/courses')} className="back-btn">
          ← Назад к курсам
        </button>

        <div className="course-header">
          {course.cover_image && (
            <img src={course.cover_image} alt={course.title} className="course-cover" />
          )}
          <div className="course-info">
            <h1>{course.title}</h1>
            <p className="course-description">{course.description}</p>
          </div>
        </div>

        <div className="lessons-section">
          <h2>Уроки курса</h2>
          
          {lessons.length > 0 ? (
            <div className="lessons-list">
              {lessons.map(lesson => (
                <div key={lesson.id} className="lesson-card">
                  <div className="lesson-info">
                    <h3>{lesson.lesson__title}</h3>
                    <Link 
                      to={`/student/courses/${courseId}/lessons/${lesson.lesson__id}`}
                      className="view-lesson-btn"
                    >
                      Перейти к уроку
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-lessons">В этом курсе пока нет уроков</p>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

// Компонент деталей урока (аналогичный вашему LessonDetail, но для ученика)
const StudentLessonDetail = () => {
  const { courseId, lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [files, setFiles] = useState([]);
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLessonData = async () => {
      try {
        const [lessonRes, filesRes, assignmentsRes] = await Promise.all([
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`),
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/files/`),
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`)
        ]);

        if (lessonRes.data?.status === 'success') {
          setLesson({
            title: lessonRes.data.lesson_title,
            description: lessonRes.data.lesson_description
          });
        }

        if (filesRes.data?.status === 'success') {
          setFiles(filesRes.data.materials || []);
        }

        if (assignmentsRes.data?.status === 'success') {
          setAssignments(assignmentsRes.data.assignments || []);
        }
      } catch (err) {
        setError('Ошибка загрузки данных урока');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchLessonData();
  }, [lessonId]);

  if (loading) return <div className="loading">Загрузка урока...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!lesson) return <div className="no-data">Урок не найден</div>;

  return (
    <div className="student-lesson-page">
      <Navbar />
      <div className="lesson-container">
        <button 
          onClick={() => navigate(`/student/courses/${courseId}`)}
          className="back-btn"
        >
          ← Назад к урокам
        </button>

        <div className="lesson-header">
          <h1>{lesson.title}</h1>
          <div className="lesson-description" dangerouslySetInnerHTML={{ __html: lesson.description }} />
        </div>

        {/* Видео материалы */}
        {files.some(f => f.filename.match(/\.(mp4|webm|ogg)$/i)) && (
          <div className="video-section">
            <h2>Видеоурок</h2>
            <div className="video-container">
              {files
                .filter(f => f.filename.match(/\.(mp4|webm|ogg)$/i))
                .map((file, index) => (
                  <video 
                    key={index} 
                    controls 
                    className="lesson-video"
                  >
                    <source src={file.url} type={`video/${file.filename.split('.').pop()}`} />
                    Ваш браузер не поддерживает видео.
                  </video>
                ))}
            </div>
          </div>
        )}

        {/* Файлы материалов */}
        {files.some(f => !f.filename.match(/\.(mp4|webm|ogg)$/i)) && (
          <div className="materials-section">
            <h2>Материалы урока</h2>
            <div className="files-list">
              {files
                .filter(f => !f.filename.match(/\.(mp4|webm|ogg)$/i))
                .map((file, index) => (
                  <div key={index} className="file-item">
                    <a 
                      href={file.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="file-link"
                    >
                      📄 {file.filename}
                    </a>
                    <span className="file-size">{(file.size / 1024 / 1024).toFixed(1)} MB</span>
                  </div>
                ))}
            </div>
          </div>
        )}

        {/* Домашние задания */}
        <div className="assignments-section">
          <h2>Домашнее задание</h2>
          {assignments.length > 0 ? (
            <div className="assignments-list">
              {assignments.map(assignment => (
                <div key={assignment.id} className="assignment-card">
                  <h3>{assignment.question}</h3>
                  {assignment.image && (
                    <img 
                      src={assignment.image} 
                      alt="Иллюстрация задания" 
                      className="assignment-image"
                    />
                  )}
                  <div className="assignment-answer">
                    <strong>Ответ:</strong> {assignment.answer}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-assignments">Для этого урока нет домашнего задания</p>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export { StudentCourses, StudentCourseDetail, StudentLessonDetail };
