import React, { useState, useEffect } from 'react';
import axios from 'axios';
//import './EditProfile.css';

const EditProfile = ({ userId }) => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        profile_photo: null,
    });
    const [message, setMessage] = useState('');

    useEffect(() => {
        // Можно подгрузить данные, если нужно
        const savedUser = JSON.parse(localStorage.getItem('user'));
        if (savedUser) {
            setFormData({
                username: savedUser.username || '',
                email: savedUser.email || '',
                profile_photo: null,
            });
        }
    }, []);

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === 'profile_photo') {
            setFormData({ ...formData, profile_photo: files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const form = new FormData();
        form.append('username', formData.username);
        form.append('email', formData.email);
        if (formData.profile_photo) {
            form.append('profile_photos', formData.profile_photo);
        }

        try {
            const response = await axios.post(
                `http://127.0.0.1:8000/accounts/api/edit/${userId}/`,
                form,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            setMessage(response.data.message);
            // Сохраняем обновленного пользователя
            localStorage.setItem('user', JSON.stringify({ ...formData, id: userId }));
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка при обновлении профиля');
        }
    };

    return (
        <div className="edit-profile-container">
            <h2>Редактировать профиль</h2>
            <form onSubmit={handleSubmit} className="edit-form">
                <label>
                    Имя пользователя:
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Фото профиля:
                    <input
                        type="file"
                        name="profile_photo"
                        accept="image/*"
                        onChange={handleChange}
                    />
                </label>
                <button type="submit">Сохранить изменения</button>
                {message && <p className="response-message">{message}</p>}
            </form>
        </div>
    );
};

export default EditProfile;
