import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLessonDetails = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
        setLesson(response.data);
        setFiles(response.data.materials || []); // –µ—Å–ª–∏ backend –æ—Ç–¥–∞–µ—Ç –º–∞—Ç–µ—Ä–∏–∞–ª—ã
      } catch (err) {
        setError('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö –∑–∞–Ω—è—Ç–∏—è');
        console.error(err);
      }
    };

    fetchLessonDetails();
  }, [lessonId]);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();

    if (!file) return alert("–í—ã–±–µ—Ä–∏—Ç–µ —Ñ–∞–π–ª");

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/upload_files/`,
        formData
      );

      if (response.data.status === 'success') {
        alert('–§–∞–π–ª —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω');
        navigate('/lessons'); // üîÅ —Ä–µ–¥–∏—Ä–µ–∫—Ç –ø–æ—Å–ª–µ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ–∞–π–ª–∞
      }
    } catch (err) {
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ —Ñ–∞–π–ª–∞');
      console.error(err);
    }
  };

  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!lesson) return <p>–ó–∞–≥—Ä—É–∑–∫–∞...</p>;

  return (
    <div>
      <Navbar />
      <div className="lesson-detail-container">
        <h2>{lesson.title}</h2>
        <p>{lesson.description}</p>

        <h3>–§–∞–π–ª—ã</h3>
        <form onSubmit={handleFileUpload}>
          <input type="file" onChange={handleFileChange} />
          <button type="submit">–ó–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–∞–π–ª</button>
        </form>

        <ul>
          {lesson.materials && lesson.materials.length > 0 ? (
  lesson.materials.map((file, index) => (
    <li key={index}>
      <a href={file.file} target="_blank" rel="noopener noreferrer">
        {file.name || '–§–∞–π–ª'}
      </a>
    </li>
  ))
) : (
  <p>–§–∞–π–ª—ã –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç</p>
)}
        </ul>
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
