import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import './LessonDetail.css';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);
  const [sections, setSections] = useState([]);
  const [selectedAssignments, setSelectedAssignments] = useState([]);
  const [attachedAssignments, setAttachedAssignments] = useState([]);
  const [expandedSections, setExpandedSections] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Получение данных урока
  useEffect(() => {
    const fetchLessonData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
        
        if (response.data.status === 'success') {
          setLesson(response.data.lesson_title);
          setDescription(response.data.lesson_description);
          setFiles(response.data.materials || []);
          setAttachedAssignments(response.data.attached_assignments || []);
        }
      } catch (err) {
        setError('Ошибка при загрузке данных занятия');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchLessonData();
  }, [lessonId]);

  // Получение разделов с заданиями
  useEffect(() => {
    const fetchSections = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/accounts/api/assignment_sections/');
        if (response.data.status === 'success') {
          setSections(response.data.sections);
        }
      } catch (err) {
        console.error('Ошибка при загрузке разделов заданий', err);
      }
    };

    fetchSections();
  }, []);

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const toggleAssignmentSelection = (assignment) => {
    setSelectedAssignments(prev => {
      const isAlreadyAttached = attachedAssignments.some(a => a.id === assignment.id);
      if (isAlreadyAttached) return prev;

      const isSelected = prev.some(a => a.id === assignment.id);
      return isSelected
        ? prev.filter(a => a.id !== assignment.id)
        : [...prev, assignment];
    });
  };

  const submitHomework = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`,
        { assignment_ids: selectedAssignments.map(a => a.id) }
      );

      if (response.data.status === 'success') {
        // Обновляем список прикрепленных заданий
        const updatedAssignments = await axios.get(
          `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`
        );
        setAttachedAssignments(updatedAssignments.data.attached_assignments || []);
        setSelectedAssignments([]);
      }
    } catch (err) {
      alert('Ошибка при сохранении домашнего задания');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const removeAssignment = async (assignmentId) => {
    try {
      setLoading(true);
      const response = await axios.delete(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/?assignment_id=${assignmentId}`
      );

      if (response.data.status === 'success') {
        // Обновляем список прикрепленных заданий
        const updatedAssignments = await axios.get(
          `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`
        );
        setAttachedAssignments(updatedAssignments.data.attached_assignments || []);
      }
    } catch (err) {
      alert('Ошибка при удалении задания');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!file) return alert("Выберите файл");

    try {
      setLoading(true);
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/upload_files/`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.status === 'success') {
        setFiles(prev => [...prev, response.data.file]);
        setFile(null);
        e.target.reset();
      }
    } catch (err) {
      alert('Ошибка при добавлении файла');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <p className="error-text">{error}</p>;
  if (!lesson) return <p className="loading-text">Загрузка данных урока...</p>;

  return (
    <div className="lesson-page">
      <Navbar />
      <div className="lesson-detail-container">
        <h1 className="lesson-title">{lesson}</h1>
        <div className="lesson-description" dangerouslySetInnerHTML={{ __html: description }} />

        {/* Секция материалов урока */}
        <div className="section materials-section">
          <h2>Материалы урока</h2>
          <form onSubmit={handleFileUpload} className="file-upload-form">
            <input 
              type="file" 
              onChange={handleFileChange} 
              disabled={loading}
            />
            <button type="submit" disabled={loading || !file}>
              {loading ? 'Загрузка...' : 'Загрузить файл'}
            </button>
          </form>

          {files.length > 0 ? (
            <div className="files-grid">
              {files.map((file, index) => (
                <div key={index} className="file-card">
                  <a
                    href={file.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="file-link"
                  >
                    <div className="file-icon">📄</div>
                    <div className="file-name">{file.filename}</div>
                  </a>
                  <div className="file-meta">
                    <span>{(file.size / 1024).toFixed(1)} KB</span>
                    <span>{new Date(file.uploaded_at).toLocaleDateString()}</span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-files">Нет загруженных материалов</p>
          )}
        </div>

        {/* Секция прикрепленных заданий */}
        <div className="section assignments-section">
          <h2>Прикрепленные задания</h2>
          {attachedAssignments.length > 0 ? (
            <div className="attached-assignments-grid">
              {attachedAssignments.map(assignment => (
                <div key={assignment.id} className="assignment-card attached">
                  <div className="assignment-header">
                    <h3 dangerouslySetInnerHTML={{ __html: assignment.question }} />
                    <button
                      onClick={() => removeAssignment(assignment.id)}
                      className="remove-btn"
                      disabled={loading}
                    >
                      ✕ Удалить
                    </button>
                  </div>
                  
                  {assignment.image && (
                    <img
                      src={assignment.image}
                      alt="Иллюстрация задания"
                      className="assignment-image"
                      onError={(e) => e.target.style.display = 'none'}
                    />
                  )}
                  
                  <div className="assignment-content">
                    <div className="answer-section">
                      <h4>Ответ:</h4>
                      <div dangerouslySetInnerHTML={{ __html: assignment.answer }} />
                    </div>
                    
                    {assignment.solution && (
                      <div className="solution-section">
                        <h4>Решение:</h4>
                        <div dangerouslySetInnerHTML={{ __html: assignment.solution }} />
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p className="no-assignments">Нет прикрепленных заданий</p>
          )}
        </div>

        {/* Секция выбора заданий */}
        <div className="section add-assignments-section">
          <h2>Добавить задания</h2>
          <div className="sections-container">
            {sections.map(section => (
              <div key={section.id} className="assignment-section">
                <div 
                  className="section-header"
                  onClick={() => toggleSection(section.id)}
                >
                  <h3>{section.title}</h3>
                  <span className="toggle-icon">
                    {expandedSections[section.id] ? '▼' : '►'}
                  </span>
                </div>
                
                {expandedSections[section.id] && (
                  <div className="assignments-list">
                    {section.assignments.map(assignment => {
                      const isAttached = attachedAssignments.some(a => a.id === assignment.id);
                      const isSelected = selectedAssignments.some(a => a.id === assignment.id);
                      
                      return (
                        <div 
                          key={assignment.id}
                          className={`assignment-item ${isAttached ? 'attached' : ''} ${isSelected ? 'selected' : ''}`}
                        >
                          <input
                            type="checkbox"
                            checked={isSelected}
                            onChange={() => toggleAssignmentSelection(assignment)}
                            disabled={isAttached}
                            id={`assignment-${assignment.id}`}
                          />
                          
                          <label htmlFor={`assignment-${assignment.id}`} className="assignment-content">
                            <div className="assignment-question" dangerouslySetInnerHTML={{ __html: assignment.question }} />
                            
                            {assignment.image && (
                              <img
                                src={assignment.image}
                                alt="Иллюстрация задания"
                                className="assignment-thumbnail"
                                onError={(e) => e.target.style.display = 'none'}
                              />
                            )}
                            
                            {isAttached && (
                              <span className="attached-badge">Уже прикреплено</span>
                            )}
                          </label>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            ))}
          </div>

          {selectedAssignments.length > 0 && (
            <div className="submit-section">
              <button
                onClick={submitHomework}
                className="submit-btn"
                disabled={loading}
              >
                {loading ? 'Сохранение...' : `Прикрепить выбранные задания (${selectedAssignments.length})`}
              </button>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
