import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const Lessons = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    video: null,
    materials: []
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (files) {
      if (name === 'materials') {
        setFormData(prev => ({
          ...prev,
          [name]: [...files] // Для множественных файлов
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          [name]: files[0] // Для одиночного файла
        }));
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);
    setSuccess(false);

    const data = new FormData();
    data.append('title', formData.title);
    data.append('description', formData.description);

    if (formData.video) {
      data.append('video', formData.video);
    }

    formData.materials.forEach(file => {
      data.append('materials', file);
    });

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/accounts/api/add_lesson/',
        data,
      );

      if (response.data.status === 'success') {
        setSuccess(true);
        // Очищаем форму после успешной отправки
        setFormData({
          title: '',
          description: '',
          video: null,
          materials: []
        });
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Произошла ошибка при отправке');
      console.error('Ошибка:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <Navbar />
      <main className="lesson-form-container">
        <h2>Добавить занятие</h2>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {success && (
          <div className="success-message">
            Занятие успешно добавлено!
          </div>
        )}

        <form onSubmit={handleSubmit} className="lesson-form">
          <div className="form-group">
            <label htmlFor="title">Название занятия*</label>
            <input
              id="title"
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              placeholder="Введите название занятия"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Описание*</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              placeholder="Добавьте описание занятия"
              rows="5"
            />
          </div>

          <div className="form-group">
            <label htmlFor="video">Видеоурок</label>
            <input
              id="video"
              type="file"
              name="video"
              accept="video/*"
              onChange={handleChange}
            />
            {formData.video && (
              <div className="file-info">
                Выбрано: {formData.video.name}
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="materials">Материалы (PDF)</label>
            <input
              id="materials"
              type="file"
              name="materials"
              accept="application/pdf"
              multiple
              onChange={handleChange}
            />
            {formData.materials.length > 0 && (
              <div className="file-info">
                Выбрано файлов: {formData.materials.length}
              </div>
            )}
          </div>

          <button
            type="submit"
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? 'Отправка...' : 'Сохранить занятие'}
          </button>
        </form>
      </main>
      <Footer />
    </div>
  );
};

export default Lessons;
