import React, { useEffect, useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
//import './Students.css'; // необязательно, если у тебя есть стили

const Students = () => {
    const [students, setStudents] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (!user || !user.is_staff) {
        navigate('/home');  // или редирект на /home
        return;
    }

    const fetchStudents = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/accounts/students/', {
                credentials: 'include',
            });

            if (!response.ok) {
                throw new Error(`Ошибка HTTP: ${response.status}`);
            }

            const data = await response.json();
            console.log('Ответ от сервера:', data); // ✅ проверка

            if (Array.isArray(data.students)) {
                setStudents(data.students);
            } else {
                throw new Error('Неверный формат данных от сервера');
            }
        } catch (err) {
            setError('Не удалось загрузить список учеников.');
            console.error(err);
        }
    };

    fetchStudents();
}, [user, navigate]);

    return (
        <div className="students-container">
            <Navbar />
            <main>
                <h2>Список учеников</h2>
                {loading ? (
                    <p>Загрузка...</p>
                ) : error ? (
                    <p style={{ color: 'red' }}>{error}</p>
                ) : students.length > 0 ? (
                    <ul className="students-list">
                        {students.map((student) => (
                            <li key={student.id} className="student-item">
                                <strong>{student.username}</strong> — {student.email}
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Ученики не найдены.</p>
                )}
            </main>
            <Footer />
        </div>
    );
};

export default Students;
