import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import './LessonDetail.css';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lessonData, setLessonData] = useState({
    title: '',
    description: '',
    materials: [],
    attached_assignments: []
  });
  const [sections, setSections] = useState([]);
  const [selectedAssignments, setSelectedAssignments] = useState([]);
  const [expandedSections, setExpandedSections] = useState({});
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Загрузка данных урока
  useEffect(() => {
    const fetchLessonData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
        
        if (response.data.status === 'success') {
          setLessonData({
            title: response.data.lesson_title || '',
            description: response.data.lesson_description || '',
            materials: response.data.materials || [],
            attached_assignments: response.data.attached_assignments || []
          });
        }
      } catch (err) {
        setError('Ошибка при загрузке данных урока');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchLessonData();
  }, [lessonId]);

  // Загрузка разделов с заданиями
  useEffect(() => {
    const fetchSections = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/accounts/api/assignment_sections/');
        if (response.data.status === 'success') {
          setSections(response.data.sections || []);
        }
      } catch (err) {
        console.error('Ошибка при загрузке разделов', err);
      }
    };

    fetchSections();
  }, []);

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const toggleAssignmentSelection = (assignment) => {
    setSelectedAssignments(prev => {
      const isAttached = lessonData.attached_assignments.some(a => a.id === assignment.id);
      if (isAttached) return prev;

      const isSelected = prev.some(a => a.id === assignment.id);
      return isSelected
        ? prev.filter(a => a.id !== assignment.id)
        : [...prev, assignment];
    });
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!file) return;

    try {
      setLoading(true);
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/upload_files/`,
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      if (response.data.status === 'success') {
        setLessonData(prev => ({
          ...prev,
          materials: [...prev.materials, response.data.file]
        }));
        setFile(null);
        e.target.reset();
      }
    } catch (err) {
      setError('Ошибка при загрузке файла');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const submitHomework = async () => {
    try {
      setLoading(true);
      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`,
        { assignment_ids: selectedAssignments.map(a => a.id) }
      );

      if (response.data.status === 'success') {
        // Обновляем данные урока
        const lessonResponse = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
        setLessonData(prev => ({
          ...prev,
          attached_assignments: lessonResponse.data.attached_assignments || []
        }));
        setSelectedAssignments([]);
      }
    } catch (err) {
      setError('Ошибка при сохранении заданий');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const removeAssignment = async (assignmentId) => {
    try {
      setLoading(true);
      const response = await axios.delete(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/?assignment_id=${assignmentId}`
      );

      if (response.data.status === 'success') {
        // Обновляем данные урока
        const lessonResponse = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`);
        setLessonData(prev => ({
          ...prev,
          attached_assignments: lessonResponse.data.attached_assignments || []
        }));
      }
    } catch (err) {
      setError('Ошибка при удалении задания');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="lesson-page">
      <Navbar />
      <div className="lesson-detail-container">
        <h1>{lessonData.title}</h1>
        <div className="lesson-description" dangerouslySetInnerHTML={{ __html: lessonData.description }} />

        {/* Материалы урока */}
        <div className="section">
          <h2>Материалы урока</h2>
          <form onSubmit={handleFileUpload} className="file-upload-form">
            <input 
              type="file" 
              onChange={(e) => setFile(e.target.files[0])} 
              disabled={loading}
            />
            <button type="submit" disabled={loading || !file}>
              {loading ? 'Загрузка...' : 'Загрузить файл'}
            </button>
          </form>

          {lessonData.materials?.length > 0 ? (
            <div className="files-grid">
              {lessonData.materials.map((file, index) => (
                <div key={index} className="file-card">
                  <a href={file.url} target="_blank" rel="noopener noreferrer">
                    {file.filename}
                  </a>
                  <div className="file-meta">
                    <span>{(file.size / 1024).toFixed(1)} KB</span>
                    <span>{new Date(file.uploaded_at).toLocaleDateString()}</span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>Нет загруженных материалов</p>
          )}
        </div>

        {/* Прикрепленные задания */}
        <div className="section">
          <h2>Прикрепленные задания</h2>
          {lessonData.attached_assignments?.length > 0 ? (
            <div className="assignments-grid">
              {lessonData.attached_assignments.map(assignment => (
                <div key={assignment.id} className="assignment-card">
                  <div className="assignment-header">
                    <h3 dangerouslySetInnerHTML={{ __html: assignment.question }} />
                    <button 
                      onClick={() => removeAssignment(assignment.id)}
                      disabled={loading}
                    >
                      Удалить
                    </button>
                  </div>
                  {assignment.image && (
                    <img 
                      src={assignment.image} 
                      alt="Иллюстрация задания" 
                      className="assignment-image"
                    />
                  )}
                  <div className="assignment-answer">
                    <strong>Ответ:</strong>
                    <div dangerouslySetInnerHTML={{ __html: assignment.answer }} />
                  </div>
                  {assignment.solution && (
                    <div className="assignment-solution">
                      <strong>Решение:</strong>
                      <div dangerouslySetInnerHTML={{ __html: assignment.solution }} />
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <p>Нет прикрепленных заданий</p>
          )}
        </div>

        {/* Добавление заданий */}
        <div className="section">
          <h2>Добавить задания</h2>
          {sections?.length > 0 ? (
            <div className="sections-list">
              {sections.map(section => (
                <div key={section.id} className="section-item">
                  <div 
                    className="section-header"
                    onClick={() => toggleSection(section.id)}
                  >
                    <h3>{section.title}</h3>
                    <span>{expandedSections[section.id] ? '▼' : '►'}</span>
                  </div>
                  
                  {expandedSections[section.id] && section.assignments?.length > 0 && (
                    <div className="assignments-list">
                      {section.assignments.map(assignment => {
                        const isAttached = lessonData.attached_assignments.some(a => a.id === assignment.id);
                        const isSelected = selectedAssignments.some(a => a.id === assignment.id);
                        
                        return (
                          <div 
                            key={assignment.id} 
                            className={`assignment-item ${isAttached ? 'attached' : ''} ${isSelected ? 'selected' : ''}`}
                          >
                            <input
                              type="checkbox"
                              id={`assign-${assignment.id}`}
                              checked={isSelected}
                              onChange={() => toggleAssignmentSelection(assignment)}
                              disabled={isAttached}
                            />
                            <label htmlFor={`assign-${assignment.id}`}>
                              <div dangerouslySetInnerHTML={{ __html: assignment.question }} />
                              {isAttached && <span className="attached-badge">(Уже прикреплено)</span>}
                            </label>
                          </div>
                        );
                      })}
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <p>Нет доступных разделов с заданиями</p>
          )}

          {selectedAssignments.length > 0 && (
            <button 
              onClick={submitHomework}
              className="submit-btn"
              disabled={loading}
            >
              Прикрепить выбранные задания ({selectedAssignments.length})
            </button>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
