import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';

const MyCourses = () => {
  const [enrollments, setEnrollments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user_id } = useParams();

  useEffect(() => {
    // Загрузка данных о курсах пользователя
    axios
      .get(`http://127.0.0.1:8000/accounts/user/${user_id}/courses/`)
      .then((response) => {
        setEnrollments(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching course details:', error);
        setError('Не удалось загрузить курсы. Попробуйте позже.');
        setLoading(false);
      });
  }, [user_id]);

  // Обработка загрузки и ошибок
  if (loading) {
    return (
      <div className="my-courses">
        <Navbar />
        <h1>Мои курсы</h1>
        <p>Загрузка...</p>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="my-courses">
        <Navbar />
        <h1>Мои курсы</h1>
        <p className="error-message">{error}</p>
        <Footer />
      </div>
    );
  }

  // Основной рендер курсов
  return (
    <div className="my-courses">
      <Navbar />
      <h1>Мои курсы</h1>
      {enrollments.length > 0 ? (
        <ul>
          {enrollments.map((course) => (
            <li key={course.id}>
              <h2>{course.title}</h2>
              <p>{course.description}</p>
              <p>Дата начала: {course.start_date}</p>
              <p>Дата окончания: {course.end_date}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>У вас пока нет курсов.</p>
      )}
      <Footer />
    </div>
  );
};

export default MyCourses;
