import React, { useState } from 'react';
import axios from 'axios';
import './Register.css';

const Register = () => {
    const [formData, setFormData] = useState({ username: '', password: '', email: '' });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        setMessage('');
    };

    const validateForm = () => {
        const { username, password, email } = formData;
        if (!username || !password || !email) {
            return "Все поля обязательны для заполнения.";
        }
        return null;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const validationError = validateForm();
        if (validationError) {
            setMessage(validationError);
            return;
        }

        try {
            const response = await axios.post('http://127.0.0.1:8000/accounts/register/', formData);
            setMessage(response.data.message);
        } catch (error) {
            setMessage(error.response?.data?.error || 'Произошла ошибка при регистрации.');
        }
    };

    return (
        <div className="register-container">
            <h1>Регистрация</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="Имя пользователя" onChange={handleChange} />
                <input type="email" name="email" placeholder="Электронная почта" onChange={handleChange} />
                <input type="password" name="password" placeholder="Пароль" onChange={handleChange} />
                <button type="submit">Зарегистрироваться</button>
            </form>
            {message && <p className="register-message">{message}</p>}
        </div>
    );
};

export default Register;
