import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';
import './LessonDetail.css';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);
  const [sections, setSections] = useState([]);
  const [selectedAssignments, setSelectedAssignments] = useState([]);
  const [attachedAssignments, setAttachedAssignments] = useState([]);
  const [expandedSections, setExpandedSections] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLessonDetails = async () => {
      try {
        const [lessonRes, filesRes, homeworkRes] = await Promise.all([
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/`),
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/files/`),
          axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`)
        ]);

        if (lessonRes.data.status === 'success') {
          setLesson(lessonRes.data.lesson_title);
          setDescription(lessonRes.data.lesson_description);
        }

        if (filesRes.data.status === 'success') {
          setFiles(filesRes.data.materials || []);
        }

        if (homeworkRes.data.status === 'success') {
          setAttachedAssignments(homeworkRes.data.assignments || []);
        }

      } catch (err) {
        setError('Ошибка при загрузке данных занятия');
        console.error(err);
      }
    };

    fetchLessonDetails();
  }, [lessonId]);

  useEffect(() => {
    const fetchSections = async () => {
      try {
        const res = await axios.get('http://127.0.0.1:8000/accounts/api/assignment_sections/');
        if (res.data.status === 'success') {
          const sectionsWithAssignments = await Promise.all(
            res.data.sections.map(async section => {
              const assignmentsRes = await axios.get(
                `http://127.0.0.1:8000/accounts/api/section_assignments/${section.id}/`
              );
              return {
                ...section,
                assignments: assignmentsRes.data.status === 'success'
                  ? assignmentsRes.data.assignments
                  : []
              };
            })
          );
          setSections(sectionsWithAssignments);
        }
      } catch (err) {
        console.error('Ошибка при загрузке разделов заданий', err);
      }
    };

    fetchSections();
  }, []);

  const toggleSection = (sectionId) => {
    setExpandedSections(prev => ({
      ...prev,
      [sectionId]: !prev[sectionId]
    }));
  };

  const toggleAssignmentSelection = (assignment) => {
    setSelectedAssignments(prev => {
      // Проверяем, не прикреплено ли уже это задание к уроку
      const isAlreadyAttached = attachedAssignments.some(a => a.id === assignment.id);
      if (isAlreadyAttached) return prev;

      // Проверяем, не выбрано ли уже это задание
      const isSelected = prev.some(a => a.id === assignment.id);

      return isSelected
        ? prev.filter(a => a.id !== assignment.id)
        : [...prev, assignment];
    });
  };

  const submitHomework = async () => {
    try {
      const res = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`,
        { assignment_ids: selectedAssignments.map(a => a.id) }
      );

      if (res.data.status === 'success') {
        setAttachedAssignments(prev => [...prev, ...selectedAssignments]);
        setSelectedAssignments([]);
        alert('Задания успешно прикреплены к уроку');
      }
    } catch (err) {
      alert('Ошибка при сохранении домашнего задания');
      console.error(err);
    }
  };

  const removeAssignment = async (assignmentId) => {
    try {
      const res = await axios.delete(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/${assignmentId}/`
      );

      if (res.data.status === 'success') {
        setAttachedAssignments(prev => prev.filter(a => a.id !== assignmentId));
        alert('Задание удалено из урока');
      }
    } catch (err) {
      alert('Ошибка при удалении задания');
      console.error(err);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (!file) return alert("Выберите файл");

    const formData = new FormData();
    formData.append('file', file);
    formData.append('name', file.name);

    try {
      const response = await axios.post(
        `http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/upload_files/`,
        formData
      );
      if (response.data.status === 'success') {
        setFiles(prev => [...prev, response.data.file]);
        alert('Файл успешно добавлен');
      }
    } catch (err) {
      alert('Ошибка при добавлении файла');
      console.error(err);
    }
  };

  if (error) return <p className="error-text">{error}</p>;
  if (!lesson) return <p className="loading-text">Загрузка...</p>;

  return (
    <div>
      <Navbar />
      <div className="lesson-detail-container">
        <h2>{lesson}</h2>
        <p className="lesson-description">{description}</p>

        <div className="section">
          <h3>Материалы урока</h3>
          <form onSubmit={handleFileUpload} className="file-upload-form">
            <input type="file" onChange={handleFileChange} />
            <button type="submit">Загрузить файл</button>
          </form>

          {files.length > 0 ? (
            <ul className="files-list">
              {files.map((file, index) => (
                <li key={index} className="file-item">
                  <a
                    href={`http://127.0.0.1:8000${file.url}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {file.filename || 'Файл'}
                  </a>
                  <div className="file-meta">
                    <span>Размер: {(file.size / 1024).toFixed(2)} Кб</span>
                    <span>Загружен: {new Date(file.uploaded_at).toLocaleString()}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>Файлы отсутствуют</p>
          )}
        </div>

        <div className="section">
          <h3>Прикрепленные задания</h3>
          {attachedAssignments.length > 0 ? (
            <div className="attached-assignments">
              {attachedAssignments.map(assignment => (
                <div key={assignment.id} className="assignment-card attached">
                  <div className="assignment-header">
                    <h4>{assignment.question}</h4>
                    <button
                      onClick={() => removeAssignment(assignment.id)}
                      className="remove-btn"
                    >
                      Удалить
                    </button>
                  </div>
                  {assignment.image && (
                    <img
                      src={`http://127.0.0.1:8000${assignment.image}`}
                      alt="assignment"
                      className="assignment-image"
                    />
                  )}
                  <div className="assignment-answer">
                    <strong>Ответ:</strong> {assignment.answer}
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>К уроку не прикреплено ни одного задания</p>
          )}
        </div>

        <div className="section">
          <h3>Добавить задания</h3>
          <div className="assignments-sections">
            {sections.map(section => (
              <div key={section.id} className="assignment-section">
                <div
                  className="section-header"
                  onClick={() => toggleSection(section.id)}
                >
                  <h4>{section.title}</h4>
                  <span>{expandedSections[section.id] ? '−' : '+'}</span>
                </div>

                {expandedSections[section.id] && (
                  <div className="section-assignments">
                    {section.assignments.map(assignment => {
                      const isAttached = attachedAssignments.some(a => a.id === assignment.id);
                      return (
                        <div
                          key={assignment.id}
                          className={`assignment-card ${isAttached ? 'attached' : ''}`}
                        >
                          <input
                            type="checkbox"
                            checked={selectedAssignments.some(a => a.id === assignment.id)}
                            onChange={() => toggleAssignmentSelection(assignment)}
                            disabled={isAttached}
                          />
                          <div className="assignment-content">
                            <h5>{assignment.question}</h5>
                            {assignment.image && (
                              <img
                                src={`http://127.0.0.1:8000/med${assignment.image}`}
                                alt="assignment"
                                className="assignment-image"
                              />
                            )}
                            <p className="assignment-answer">
                              <strong>Ответ:</strong> {assignment.answer}
                            </p>
                            {isAttached && (
                              <span className="already-attached">(Уже прикреплено)</span>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            ))}
          </div>

          {selectedAssignments.length > 0 && (
            <div className="submit-homework">
              <button
                onClick={submitHomework}
                className="submit-btn"
              >
                Прикрепить выбранные задания ({selectedAssignments.length})
              </button>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
