import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Catalog = () => {
  const [courses, setCourses] = useState([]); // Список курсов
  const [loading, setLoading] = useState(true); // Состояние загрузки
  const [error, setError] = useState(null); // Состояние ошибки

  useEffect(() => {
    // Загрузка курсов с бэкенда
    axios
      .get('http://127.0.0.1:8000/accounts/courses/')
      .then((response) => {
        setCourses(response.data); // Устанавливаем полученные курсы
        setLoading(false); // Завершаем загрузку
      })
      .catch((error) => {
        console.error('Error fetching courses:', error);
        setError('Не удалось загрузить курсы. Попробуйте позже.');
        setLoading(false); // Завершаем загрузку с ошибкой
      });
  }, []);

  // Отображение в зависимости от состояния
  if (loading) {
    return <p>Загрузка курсов...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (courses.length === 0) {
    return <p>Курсы не найдены.</p>;
  }

  return (
    <div className="catalog">
      <h1>Каталог курсов</h1>
      <ul>
        {courses.map((course) => (
          <li key={course.id}>
            <h2>{course.title}</h2>
            <p>{course.description}</p>
            <p>Максимальное количество участников: {course.max_participants}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Catalog;
