import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const LessonDetail = () => {
  const { lessonId } = useParams();
  const [lesson, setLesson] = useState(null);
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState([]);
  const [homework, setHomework] = useState([]);
  const [error, setError] = useState('');
  const [loadingHomework, setLoadingHomework] = useState(true);

  useEffect(() => {
    const fetchLessonDetails = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/files/`);
        if (response.data.status === 'success') {
          setLesson(response.data.lesson_title);
          setDescription(response.data.lesson_description || '');
          setFiles(response.data.materials || []);
        } else {
          setError(response.data.message || 'Ошибка при загрузке файлов');
        }
      } catch (err) {
        setError('Ошибка при загрузке данных занятия');
        console.error(err);
      }
    };

    const fetchHomework = async () => {
      try {
        setLoadingHomework(true);
        const response = await axios.get(`http://127.0.0.1:8000/accounts/api/lessons/${lessonId}/homework/`);
        if (response.data.status === 'success') {
          // Добавляем полные URL для изображений
          const processedHomework = response.data.homework.map(item => ({
            ...item,
            imageUrl: item.image ? `http://127.0.0.1:8000/media/${item.image}` : null
          }));
          setHomework(processedHomework || []);
        }
      } catch (err) {
        console.error('Ошибка при загрузке домашнего задания:', err);
      } finally {
        setLoadingHomework(false);
      }
    };

    fetchLessonDetails();
    fetchHomework();
  }, [lessonId]);

  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!lesson) return <p>Загрузка...</p>;

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Navbar />
      <div className="lesson-detail-container" style={{ flex: 1, padding: '20px' }}>
        <h2>{lesson}</h2>
        {description && <p>{description}</p>}

        <h3>Файлы</h3>
        {files.length > 0 ? (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {files.map((file, index) => (
              <li key={index} style={{ marginBottom: '10px' }}>
                <a
                  href={`http://127.0.0.1:8000${file.url}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ color: '#0066cc', textDecoration: 'none' }}
                >
                  {file.filename || 'Файл'}
                </a>
                <div style={{ fontSize: '0.8em', color: '#666' }}>
                  Размер: {(file.size / 1024).toFixed(2)} KB<br />
                  Загружен: {new Date(file.uploaded_at).toLocaleString()}
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>Файлы отсутствуют</p>
        )}

        <h3 style={{ marginTop: '30px' }}>Домашнее задание</h3>
        {loadingHomework ? (
          <p>Загрузка домашнего задания...</p>
        ) : homework.length > 0 ? (
          <div style={{ marginTop: '15px' }}>
            {homework.map((item, index) => (
              <div
                key={index}
                style={{
                  marginBottom: '25px',
                  padding: '15px',
                  border: '1px solid #eee',
                  borderRadius: '5px'
                }}
              >
                <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>Вопрос: {item.question}</div>
                {item.imageUrl && (
                  <div style={{ margin: '10px 0' }}>
                    <img
                      src={item.imageUrl}
                      alt="Иллюстрация к заданию"
                      style={{
                        maxWidth: '100%',
                        maxHeight: '300px',
                        border: '1px solid #ddd',
                        borderRadius: '4px'
                      }}
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                  </div>
                )}
                <div style={{ marginTop: '10px' }}>
                  <span style={{ fontWeight: 'bold' }}>Ответ:</span> {item.answer}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>Домашнее задание отсутствует</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default LessonDetail;
