import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const MyCourses = () => {
  const [user, setUser] = useState(null);
  const [enrollments, setEnrollments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserCourses = async () => {
      try {
        const storedUser = localStorage.getItem('user');
        const userData = JSON.parse(storedUser);
        setUser(userData);
        // Запрос на сервер для получения информации о текущем пользователе
        //const userId = user.id;

        // Используем полученный userId для загрузки курсов
        const coursesResponse = await axios.get(`http://127.0.0.1:8000/accounts/user/${user.id}/courses/`);
        setEnrollments(coursesResponse.data);
      } catch (err) {
        setError('Не удалось загрузить курсы.');
        console.error('Error fetching user courses:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserCourses();
  }, []);

  if (loading) return <div>Загрузка...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="my-courses">
      <Navbar />
      <h1>Мои курсы</h1>
      <ul>
        {enrollments.length > 0 ? (
          enrollments.map((course) => (
            <li key={course.id}>
              <h2>{course.title}</h2>
              <p>{course.description}</p>
            </li>
          ))
        ) : (
          <p>У вас пока нет курсов.</p>
        )}
      </ul>
      <Footer />
    </div>
  );
};

export default MyCourses;
