import React from 'react';
import './Header.css';

const Header = ({ isLoggedIn }) => {
    return (
        <header className="header">
            <div className="logo">GameWise</div>
            <nav className="nav">
                <button className="nav-button">Каталог</button>
                <button className="nav-button">Мои курсы</button>
                <button className="nav-button">О проекте</button>
            </nav>
            {!isLoggedIn && (
                <button className="signup-button" onClick={() => window.location.href = '/signup'}>
                    Sign In
                </button>
            )}
        </header>
    );
};

export default Header;
