import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const AssignmentPage = () => {
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newAssignment, setNewAssignment] = useState({
    question: '',
    answer: '',
    image: null
  });

  useEffect(() => {
    fetchAssignments();
  }, []);

  const fetchAssignments = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/accounts/api/assignments/');

      if (response.data.status === 'success') {
        const processedAssignments = response.data.assignments.map(assignment => ({
          ...assignment,
          imageUrl: assignment.image
            ? `http://127.0.0.1:8000/media/${assignment.image}`
            : null
        }));

        setAssignments(processedAssignments);
      }
    } catch (error) {
      console.error('Ошибка при загрузке заданий:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAssignment(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setNewAssignment(prev => ({
      ...prev,
      image: e.target.files[0]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('question', newAssignment.question);
    formData.append('answer', newAssignment.answer);
    if (newAssignment.image) {
      formData.append('image', newAssignment.image);
    }

    try {
      await axios.post('http://127.0.0.1:8000/accounts/api/assignments/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      fetchAssignments();
      setShowAddForm(false);
      setNewAssignment({
        question: '',
        answer: '',
        image: null
      });
    } catch (error) {
      console.error('Ошибка при добавлении задания:', error);
    }
  };

  if (loading) {
    return <div>Загрузка заданий...</div>;
  }

  return (
    <div>
    <Navbar />
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h1 style={{ marginBottom: '30px' }}>Задания</h1>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          style={{
            padding: '8px 16px',
            backgroundColor: showAddForm ? '#f44336' : '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            height: '40px'
          }}
        >
          {showAddForm ? 'Отменить' : 'Добавить задание'}
        </button>
      </div>

      {showAddForm && (
        <div style={{
          marginBottom: '30px',
          padding: '20px',
          border: '1px solid #e0e0e0',
          borderRadius: '8px'
        }}>
          <h2>Добавить новое задание</h2>
          <form onSubmit={handleSubmit}>
            <div style={{ marginBottom: '15px' }}>
              <label>Вопрос:</label>
              <input
                type="text"
                name="question"
                value={newAssignment.question}
                onChange={handleInputChange}
                required
                style={{ width: '100%', padding: '8px' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label>Ответ:</label>
              <textarea
                name="answer"
                value={newAssignment.answer}
                onChange={handleInputChange}
                required
                style={{ width: '100%', padding: '8px', minHeight: '100px' }}
              />
            </div>
            <div style={{ marginBottom: '15px' }}>
              <label>Изображение:</label>
              <input
                type="file"
                onChange={handleFileChange}
                accept="image/*"
              />
            </div>
            <button
              type="submit"
              style={{
                padding: '8px 16px',
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Сохранить
            </button>
          </form>
        </div>
      )}

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
        gap: '20px'
      }}>
        {assignments.map((assignment) => (
          <div
            key={assignment.id}
            style={{
              border: '1px solid #e0e0e0',
              borderRadius: '8px',
              padding: '20px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              position: 'relative'
            }}
          >
            <div style={{
              position: 'absolute',
              top: '-10px',
              left: '-10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              borderRadius: '50%',
              width: '30px',
              height: '30px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: 'bold',
              boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
            }}>
              {assignment.id}
            </div>

            <h3 style={{ marginTop: '10px' }}>{assignment.question}</h3>

            {assignment.imageUrl && (
              <div style={{
                margin: '15px 0',
                borderRadius: '4px',
                overflow: 'hidden'
              }}>
                <img
                  src={assignment.imageUrl}
                  alt={`Иллюстрация к заданию: ${assignment.question}`}
                  style={{
                    width: '100%',
                    height: 'auto',
                    display: 'block'
                  }}
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              </div>
            )}

            <div style={{
              backgroundColor: '#f5f5f5',
              padding: '12px',
              borderRadius: '4px',
              marginTop: '10px'
            }}>
              <p style={{ margin: 0 }}>{assignment.answer}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AssignmentPage;
