import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const Lessons = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [video, setVideo] = useState(null);
  const [materials, setMaterials] = useState([]); // список файлов

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);

    if (video) formData.append('video', video);
    materials.forEach((file) => formData.append('materials', file)); // добавляем все файлы

    try {
      const res = await axios.post('http://127.0.0.1:8000/accounts/api/add_lesson/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Занятие успешно добавлено');
    } catch (error) {
      alert('Ошибка при добавлении занятия');
      console.error(error);
    }
  };

  return (
    <div>
      <Navbar />
      <main>
        <h2>Добавить занятие</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Название занятия"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Описание"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="file"
            accept="video/*"
            onChange={(e) => setVideo(e.target.files[0])}
          />
          <input
            type="file"
            accept="application/pdf"
            multiple
            onChange={(e) => setMaterials([...e.target.files])} // сохраняем все выбранные файлы
          />
          <button type="submit">Сохранить</button>
        </form>
      </main>
      <Footer />
    </div>
  );
};

export default Lessons;
