import React, { useState } from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import Footer from './Footer';

const Lessons = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [video, setVideo] = useState(null);
  const [material, setMaterial] = useState(null); // один файл

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    if (video) formData.append('video', video);
    if (material) formData.append('materials', material); // только один файл

    try {
      const response = await axios.post('http://127.0.0.1:8000/accounts/api/add_lesson/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        withCredentials: true, // если используется авторизация через сессию
      });
      alert('Занятие успешно добавлено!');
      // сброс формы
      setTitle('');
      setDescription('');
      setVideo(null);
      setMaterial(null);
    } catch (error) {
      console.error('Ошибка при отправке:', error.response?.data || error.message);
      alert('Ошибка при добавлении занятия');
    }
  };

  return (
    <div>
      <Navbar />
      <main style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
        <h2>Добавить занятие</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Название занятия</label>
            <input
              type="text"
              value={title}
              onChange={e => setTitle(e.target.value)}
              required
              style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
            />
          </div>
          <div>
            <label>Описание</label>
            <textarea
              value={description}
              onChange={e => setDescription(e.target.value)}
              rows={4}
              style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
            />
          </div>
          <div>
            <label>Видео (необязательно)</label>
            <input
              type="file"
              accept="video/*"
              onChange={e => setVideo(e.target.files[0])}
              style={{ marginBottom: '10px' }}
            />
          </div>
          <div>
            <label>Материал (PDF)</label>
            <input
              type="file"
              accept="application/pdf"
              onChange={e => setMaterial(e.target.files[0])}
              style={{ marginBottom: '10px' }}
            />
          </div>
          <button type="submit">Сохранить</button>
        </form>
      </main>
      <Footer />
    </div>
  );
};

export default Lessons;
