import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './CreateCourse.css';

const CourseCreator = () => {
    const [courses, setCourses] = useState([]);
    const [lessons, setLessons] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [newCourse, setNewCourse] = useState({
        title: '',
        description: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const [coursesRes, lessonsRes] = await Promise.all([
                    axios.get('http://127.0.0.1:8000/accounts/api/courses/'),
                    axios.get('http://127.0.0.1:8000/accounts/api/lessons/')
                ]);

                if (coursesRes.data.status === 'success') {
                    setCourses(coursesRes.data.courses);
                }

                if (lessonsRes.data.status === 'success') {
                    setLessons(lessonsRes.data.lessons);
                }
            } catch (err) {
                setError('Ошибка загрузки данных');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleCreateCourse = async () => {
        try {
            setLoading(true);
            const response = await axios.post(
                'http://127.0.0.1:8000/accounts/api/courses/',
                newCourse,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'X-CSRFToken': getCookie('csrftoken')
                    }
                }
            );

            if (response.data.status === 'success') {
                setCourses(prev => [...prev, {
                    id: response.data.course_id,
                    ...newCourse
                }]);
                setNewCourse({ title: '', description: '' });
            }
        } catch (err) {
            setError('Ошибка создания курса');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const addLessonToCourse = async (lessonId) => {
        try {
            const response = await axios.post(
                `http://127.0.0.1:8000/accounts/api/courses/${selectedCourse.id}/lessons/`,
                { lesson_id: lessonId },
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'X-CSRFToken': getCookie('csrftoken')
                    }
                }
            );

            if (response.data.status === 'success') {
                const updatedCourse = await axios.get(
                    `http://127.0.0.1:8000/accounts/api/courses/${selectedCourse.id}/`
                );
                setSelectedCourse(updatedCourse.data.course);
            }
        } catch (err) {
            setError('Ошибка добавления урока');
            console.error(err);
        }
    };

    const removeLessonFromCourse = async (lessonId) => {
        try {
            const response = await axios.delete(
                `http://127.0.0.1:8000/accounts/api/courses/${selectedCourse.id}/lessons/${lessonId}/`,
                {
                    headers: {
                        'X-CSRFToken': getCookie('csrftoken')
                    }
                }
            );

            if (response.data.status === 'success') {
                const updatedCourse = await axios.get(
                    `http://127.0.0.1:8000/accounts/api/courses/${selectedCourse.id}/`
                );
                setSelectedCourse(updatedCourse.data.course);
            }
        } catch (err) {
            setError('Ошибка удаления урока');
            console.error(err);
        }
    };

    const onDragEnd = async (result) => {
        if (!result.destination || !selectedCourse) return;

        const reorderedLessons = Array.from(selectedCourse.lessons);
        const [removed] = reorderedLessons.splice(result.source.index, 1);
        reorderedLessons.splice(result.destination.index, 0, removed);

        try {
            await axios.put(
                `http://127.0.0.1:8000/accounts/api/courses/${selectedCourse.id}/`,
                {
                    lessons_order: reorderedLessons.map((lesson, index) => ({
                        lesson_id: lesson.lesson__id,
                        order: index + 1
                    }))
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'X-CSRFToken': getCookie('csrftoken')
                    }
                }
            );

            const updatedCourse = await axios.get(
                `http://127.0.0.1:8000/api/courses/${selectedCourse.id}/`
            );
            setSelectedCourse(updatedCourse.data.course);
        } catch (err) {
            setError('Ошибка изменения порядка уроков');
            console.error(err);
        }
    };

    // Функция для получения CSRF токена
    function getCookie(name) {
        let cookieValue = null;
        if (document.cookie && document.cookie !== '') {
            const cookies = document.cookie.split(';');
            for (let i = 0; i < cookies.length; i++) {
                const cookie = cookies[i].trim();
                if (cookie.substring(0, name.length + 1) === (name + '=')) {
                    cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                    break;
                }
            }
        }
        return cookieValue;
    }

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="course-creator">
            <div className="courses-list">
                <h2>Мои курсы</h2>
                <div className="create-course-form">
                    <input
                        type="text"
                        placeholder="Название курса"
                        value={newCourse.title}
                        onChange={(e) => setNewCourse({ ...newCourse, title: e.target.value })}
                    />
                    <textarea
                        placeholder="Описание курса"
                        value={newCourse.description}
                        onChange={(e) => setNewCourse({ ...newCourse, description: e.target.value })}
                    />
                    <button onClick={handleCreateCourse}>Создать курс</button>
                </div>

                <ul>
                    {courses.map(course => (
                        <li
                            key={course.id}
                            className={selectedCourse?.id === course.id ? 'active' : ''}
                            onClick={() => setSelectedCourse(course)}
                        >
                            {course.title}
                        </li>
                    ))}
                </ul>
            </div>

            {selectedCourse && (
                <div className="course-editor">
                    <h2>Редактирование: {selectedCourse.title}</h2>

                    <div className="course-lessons">
                        <h3>Уроки в курсе</h3>
                        <DragDropContext onDragEnd={onDragEnd}>
                            <Droppable droppableId="lessons">
                                {(provided) => (
                                    <div
                                        {...provided.droppableProps}
                                        ref={provided.innerRef}
                                        className="lessons-container"
                                    >
                                        {selectedCourse.lessons.length > 0 ? (
                                            selectedCourse.lessons.map((lesson, index) => (
                                                <Draggable
                                                    key={lesson.lesson__id}
                                                    draggableId={String(lesson.lesson__id)}
                                                    index={index}
                                                >
                                                    {(provided) => (
                                                        <div
                                                            ref={provided.innerRef}
                                                            {...provided.draggableProps}
                                                            {...provided.dragHandleProps}
                                                            className="lesson-item"
                                                        >
                                                            <span>{lesson.lesson__title}</span>
                                                            <button
                                                                onClick={() => removeLessonFromCourse(lesson.lesson__id)}
                                                                className="remove-btn"
                                                            >
                                                                Удалить
                                                            </button>
                                                        </div>
                                                    )}
                                                </Draggable>
                                            ))
                                        ) : (
                                            <p>В курсе пока нет уроков</p>
                                        )}
                                        {provided.placeholder}
                                    </div>
                                )}
                            </Droppable>
                        </DragDropContext>
                    </div>

                    <div className="available-lessons">
                        <h3>Доступные уроки</h3>
                        <ul>
                            {lessons
                                .filter(lesson =>
                                    !selectedCourse.lessons.some(l => l.lesson__id === lesson.id)
                                )
                                .map(lesson => (
                                    <li key={lesson.id}>
                                        <span>{lesson.title}</span>
                                        <button
                                            onClick={() => addLessonToCourse(lesson.id)}
                                            className="add-btn"
                                        >
                                            Добавить
                                        </button>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CourseCreator;
