import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AssignmentPage = () => {
  const [assignments, setAssignments] = useState([]);
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [image, setImage] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showAddForm, setShowAddForm] = useState(false); // Состояние для показа/скрытия формы добавления

  // Загружаем задания
  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/accounts/api/assignments/');
        setAssignments(response.data);
      } catch (err) {
        setError('Ошибка при загрузке заданий');
        console.error(err);
      }
    };

    fetchAssignments();
  }, []);

  // Обработчик изменения файла
  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!question || !answer) {
      setError('Пожалуйста, заполните все поля');
      return;
    }

    const formData = new FormData();
    formData.append('question', question);
    formData.append('answer', answer);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/accounts/api/assignments/', formData);
      if (response.data.status === 'success') {
        setSuccess('Задание успешно добавлено!');
        setQuestion('');
        setAnswer('');
        setImage(null);
        // Перезагружаем список заданий после добавления нового
        const updatedAssignments = await axios.get('http://127.0.0.1:8000/accounts/api/assignments/');
        setAssignments(updatedAssignments.data);
      } else {
        setError('Ошибка при добавлении задания');
      }
    } catch (err) {
      setError('Ошибка при отправке данных');
      console.error(err);
    }
  };

  return (
    <div>
      <h1>Задания</h1>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <h2>Список заданий</h2>
      <ul>
        {assignments.length > 0 ? (
          assignments.map((assignment) => (
            <li key={assignment.id}>
              <h3>{assignment.question}</h3>
              <p>{assignment.answer}</p>
              {assignment.image && <img src={`http://127.0.0.1:8000${assignment.image}`} alt="Задание" />}
            </li>
          ))
        ) : (
          <p>Задания не найдены</p>
        )}
      </ul>

      <button onClick={() => setShowAddForm(!showAddForm)}>
        {showAddForm ? 'Отменить добавление' : 'Добавить задание'}
      </button>

      {showAddForm && (
        <div>
          <h2>Добавить задание</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Вопрос:</label>
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Ответ:</label>
              <textarea
                value={answer}
                onChange={(e) => setAnswer(e.target.value)}
                required
              />
            </div>
            <div>
              <label>Изображение:</label>
              <input type="file" onChange={handleFileChange} />
            </div>
            <button type="submit">Добавить задание</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AssignmentPage;
