import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AssignmentPage = () => {
  const [assignments, setAssignments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAssignments = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/accounts/api/assignments/');

        if (response.data.status === 'success') {
          const processedAssignments = response.data.assignments.map(assignment => ({
            ...assignment,
            imageUrl: assignment.image
              ? `http://127.0.0.1:8000/media/${assignment.image}`
              : null
          }));

          setAssignments(processedAssignments);
        }
      } catch (error) {
        console.error('Ошибка при загрузке заданий:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAssignments();
  }, []);

  if (loading) {
    return <div>Загрузка заданий...</div>;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>Задания</h1>

      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
        gap: '20px'
      }}>
        {assignments.map((assignment) => (
          <div
            key={assignment.id}
            style={{
              border: '1px solid #e0e0e0',
              borderRadius: '8px',
              padding: '20px',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              position: 'relative' // Для позиционирования ID
            }}
          >
            {/* ID задачи в левом верхнем углу */}
            <div style={{
              position: 'absolute',
              top: '-10px',
              left: '-10px',
              backgroundColor: '#4CAF50',
              color: 'white',
              borderRadius: '50%',
              width: '30px',
              height: '30px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontWeight: 'bold',
              boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
            }}>
              {assignment.id}
            </div>

            <h3 style={{ marginTop: '10px' }}>{assignment.question}</h3>

            {assignment.imageUrl && (
              <div style={{
                margin: '15px 0',
                borderRadius: '4px',
                overflow: 'hidden'
              }}>
                <img
                  src={assignment.imageUrl}
                  alt={`Иллюстрация к заданию: ${assignment.question}`}
                  style={{
                    width: '100%',
                    height: 'auto',
                    display: 'block'
                  }}
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              </div>
            )}

            <div style={{
              backgroundColor: '#f5f5f5',
              padding: '12px',
              borderRadius: '4px',
              marginTop: '10px'
            }}>
              <p style={{ margin: 0 }}>{assignment.answer}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AssignmentPage;
