import React, { useEffect, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.css';
import { UserContext } from './UserContext';
import Navbar from './Navbar';
import Footer from './Footer';

const Profile = () => {
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        profile_photo: null,
        newPhotoPreview: null
    });
    const navigate = useNavigate();
    const { user: contextUser, logout, login } = useContext(UserContext);

    useEffect(() => {
        if (!contextUser) {
            setError('Пользователь не найден. Авторизуйтесь.');
            setLoading(false);
            return navigate('/login');
        }

        setFormData({
            username: contextUser.username,
            email: contextUser.email,
            profile_photo: null,
            newPhotoPreview: null
        });
        setLoading(false);
    }, [contextUser, navigate]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setFormData({
                ...formData,
                profile_photo: file,
                newPhotoPreview: URL.createObjectURL(file)
            });
        }
    };

    const handleSave = async () => {
        if (!contextUser) return;

        const formDataToSend = new FormData();
        formDataToSend.append('username', formData.username);
        formDataToSend.append('email', formData.email);
        if (formData.profile_photo) {
            formDataToSend.append('profile_photo', formData.profile_photo);
        }

        try {
            const response = await axios.put(
                `http://127.0.0.1:8000/accounts/api/users/${contextUser.id}/`,
                formDataToSend,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            if (response.data.status === 'success') {
                // Обновляем данные пользователя в контексте и localStorage
                const updatedUser = {
                    ...contextUser,
                    username: formData.username,
                    email: formData.email,
                    profile_photo: response.data.profile_photo_url || contextUser.profile_photo,
                };

                login(updatedUser); // Обновляем контекст

                setIsEditing(false);
                setFormData(prev => ({
                    ...prev,
                    newPhotoPreview: null
                }));
            } else {
                setError(response.data.message || 'Ошибка при обновлении профиля');
            }
        } catch (error) {
            setError(error.response?.data?.message || 'Ошибка при отправке данных на сервер');
            console.error('Update error:', error);
        }
    };

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (loading) return <div className="loading">Загрузка...</div>;
    if (error) return <div className="error">{error}</div>;
    if (!contextUser) return <div className="error">Данные профиля не найдены.</div>;

    return (
        <div className="profile-container">
            <Navbar />
            <div className="profile-content">
                <div className="profile-header">
                    <img
                        src={formData.newPhotoPreview ||
                            (contextUser.profile_photo ?
                                `http://127.0.0.1:8000${contextUser.profile_photo}` :
                                '/default-profile.png')
                        }
                        alt="Profile"
                        className="profile-photo"
                    />
                    <h2>{contextUser.username}</h2>
                </div>

                {isEditing ? (
                    <div className="edit-form">
                        <div className="form-group">
                            <label>Имя пользователя:</label>
                            <input
                                type="text"
                                name="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                className="form-input"
                            />
                        </div>
                        <div className="form-group">
                            <label>Email:</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className="form-input"
                            />
                        </div>
                        <div className="form-group">
                            <label>Фото профиля:</label>
                            <input
                                type="file"
                                name="profile_photo"
                                accept="image/*"
                                onChange={handleFileChange}
                                className="form-input"
                            />
                        </div>
                        <div className="form-actions">
                            <button
                                className="btn save-btn"
                                onClick={handleSave}
                                disabled={!formData.username || !formData.email}
                            >
                                Сохранить
                            </button>
                            <button
                                className="btn cancel-btn"
                                onClick={() => {
                                    setIsEditing(false);
                                    setFormData({
                                        username: contextUser.username,
                                        email: contextUser.email,
                                        profile_photo: null,
                                        newPhotoPreview: null
                                    });
                                }}
                            >
                                Отмена
                            </button>
                        </div>
                    </div>
                ) : (
                    <div className="profile-details">
                        <p><strong>Email:</strong> {contextUser.email}</p>
                        <div className="profile-actions">
                            <button
                                className="btn edit-btn"
                                onClick={() => setIsEditing(true)}
                            >
                                Редактировать профиль
                            </button>
                            <button
                                className="btn logout-btn"
                                onClick={handleLogout}
                            >
                                Выйти
                            </button>
                        </div>
                    </div>
                )}
            </div>
            <Footer />
        </div>
    );
};

export default Profile;
